DROP TABLE DEV.US_500 PURGE;

CREATE TABLE DEV.US_500 (
  "ID" NUMBER GENERATED BY DEFAULT AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER NOCYCLE NOKEEP NOSCALE NOT NULL ENABLE
  , "EMPLOYEE_FIRST_NAME" VARCHAR2(60) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "EMPLOYEE_LAST_NAME" VARCHAR2(60) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "COMPANY_NAME" VARCHAR2(60) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "COMPANY_ADDRESS" VARCHAR2(60) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "COMPANY_CITY" VARCHAR2(60) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "COMPANY_COUNTY" VARCHAR2(60) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "COMPANY_STATE" VARCHAR2(60) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "COMPANY_ZIP" VARCHAR2(60) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "COMPANY_PHONE1" VARCHAR2(60) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "PERSONAL_PHONE2" VARCHAR2(60) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "PERSONAL_EMAIL" VARCHAR2(60) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "COMPANY_WEB" VARCHAR2(60) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "CREATE_USER" VARCHAR2(8) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "CREATE_TIME" TIMESTAMP (0) DEFAULT NULL
  , "UPDATE_USER" VARCHAR2(8) COLLATE "USING_NLS_COMP" DEFAULT NULL
  , "UPDATE_TIME" TIMESTAMP (0) DEFAULT NULL
  , PRIMARY KEY ("ID")
  ) TABLESPACE "SAMPLESCHEMA" NOLOGGING;

COMMENT ON TABLE DEV.US_500 IS 'bd01_us_500.csv';
COMMENT ON COLUMN DEV.US_500.ID IS 'ID';
COMMENT ON COLUMN DEV.US_500.EMPLOYEE_FIRST_NAME IS '이름';
COMMENT ON COLUMN DEV.US_500.EMPLOYEE_LAST_NAME IS '성';
COMMENT ON COLUMN DEV.US_500.COMPANY_NAME IS '회사명';
COMMENT ON COLUMN DEV.US_500.COMPANY_ADDRESS IS '회사 주소';
COMMENT ON COLUMN DEV.US_500.COMPANY_CITY IS '회사 도시';
COMMENT ON COLUMN DEV.US_500.COMPANY_COUNTY IS '회사 카운티';
COMMENT ON COLUMN DEV.US_500.COMPANY_STATE IS '회사 주';
COMMENT ON COLUMN DEV.US_500.COMPANY_ZIP IS '회사 우편번호';
COMMENT ON COLUMN DEV.US_500.COMPANY_PHONE1 IS '회사 전화번호';
COMMENT ON COLUMN DEV.US_500.PERSONAL_PHONE2 IS '개인 전화번호';
COMMENT ON COLUMN DEV.US_500.PERSONAL_EMAIL IS '개인 이메일';
COMMENT ON COLUMN DEV.US_500.COMPANY_WEB IS '회사 URL';
COMMENT ON COLUMN DEV.US_500.CREATE_USER IS '최초 등록 사용자';
COMMENT ON COLUMN DEV.US_500.CREATE_TIME IS '최초 등록 시간';
COMMENT ON COLUMN DEV.US_500.UPDATE_USER IS '최종 수정 사용자';
COMMENT ON COLUMN DEV.US_500.UPDATE_TIME IS '최종 수정 시간';

SELECT 'ALTER INDEX ' || A.INDEX_NAME || ' RENAME TO US_500_X01;'
FROM ALL_IND_COLUMNS A
  , ALL_INDEXES B
WHERE A.INDEX_NAME = B.INDEX_NAME
  AND A.TABLE_NAME = 'US_500'
  AND A.COLUMN_NAME = 'ID';
