mapper.insert:
INSERT INTO
DEV.SYSTEM_EVENTS (
CUSTOMER_ID
, RECEIVED_AT
, DEVICE_REPORTED_TIME
, FACILITY
, PRIORITY
, FROM_HOST
, MESSAGE
, NT_SEVERITY
, IMPORTANCE
, EVENT_SOURCE
, EVENT_USER
, EVENT_CATEGORY
, EVENT_ID
, EVENT_BINARY_DATA
, MAX_AVAILABLE
, CURR_USAGE
, MIN_USAGE
, MAX_USAGE
, INFO_UNIT_ID
, SYS_LOG_TAG
, EVENT_LOG_TYPE
, GENERIC_FILE_NAME
, SYSTEM_ID
, CREATE_USER
, CREATE_TIME
)
VALUES (
#{customerId}
, to_date(#{receivedAt}, 'YYYY-MM-DD HH24:MI:SS')
, to_date(#{deviceReportedTime}, 'YYYY-MM-DD HH24:MI:SS')
, #{facility}
, #{priority}
, #{fromHost}
, #{message}
, #{ntSeverity}
, #{importance}
, #{eventSource}
, #{eventUser}
, #{eventCategory}
, #{eventId}
, #{eventBinaryData}
, #{maxAvailable}
, #{currUsage}
, #{minUsage}
, #{maxUsage}
, #{infoUnitId}
, #{sysLogTag}
, #{eventLogType}
, #{genericFileName}
, #{systemId}
, #{createUser}
, CURRENT_TIMESTAMP
)

mapper.update:
UPDATE
DEV.SYSTEM_EVENTS
SET
CUSTOMER_ID = #{customerId}
, RECEIVED_AT = to_date(#{receivedAt}, 'YYYY-MM-DD HH24:MI:SS')
, DEVICE_REPORTED_TIME = to_date(#{deviceReportedTime}, 'YYYY-MM-DD HH24:MI:SS')
, FACILITY = #{facility}
, PRIORITY = #{priority}
, FROM_HOST = #{fromHost}
, MESSAGE = #{message}
, NT_SEVERITY = #{ntSeverity}
, IMPORTANCE = #{importance}
, EVENT_SOURCE = #{eventSource}
, EVENT_USER = #{eventUser}
, EVENT_CATEGORY = #{eventCategory}
, EVENT_ID = #{eventId}
, EVENT_BINARY_DATA = #{eventBinaryData}
, MAX_AVAILABLE = #{maxAvailable}
, CURR_USAGE = #{currUsage}
, MIN_USAGE = #{minUsage}
, MAX_USAGE = #{maxUsage}
, INFO_UNIT_ID =  #{infoUnitId}
, SYS_LOG_TAG = #{sysLogTag}
, EVENT_LOG_TYPE = #{eventLogType}
, GENERIC_FILE_NAME = #{genericFileName}
, SYSTEM_ID = #{systemId}
, UPDATE_USER = #{updateUser}
, UPDATE_TIME = CURRENT_TIMESTAMP
WHERE
1 = 1
AND ID = #{id}
