  , EVENT_SOURCE
  , EVENT_USER
  , EVENT_CATEGORY
  , EVENT_ID
  , EVENT_BINARY_DATA
  , MAX_AVAILABLE
  , CURR_USAGE
  , MIN_USAGE
  , MAX_USAGE
  , INFO_UNIT_ID
  , SYS_LOG_TAG
  , EVENT_LOG_TYPE
  , GENERIC_FILE_NAME
  , SYSTEM_ID
  )
VALUES (
  #{customerId}
  , #{receivedAt}
  , #{deviceReportedTime}
  , #{facility}
  , #{priority}
  , #{fromHost}
  , #{message}
  , #{ntSeverity}
  , #{importance}
  , #{eventSource}
  , #{eventUser}
  , #{eventCategory}
  , #{eventId}
  , #{eventBinaryData}
  , #{maxAvailable}
  , #{currUsage}
  , #{minUsage}
  , #{maxUsage}
  , #{infoUnitId}
  , #{sysLogTag}
  , #{eventLogType}
  , #{genericFileName}
  , #{systemId}
  )

